const fs = require('fs');
const React = require('react');
const { Provider } = require('react-redux');
const { renderToString } = require('react-dom/server');
const { match, RouterContext } = require('react-router');
const routes = require('./routes.js');

/* eslint-disable no-sync */
const template = fs.readFileSync(__dirname + '/../index.html', 'utf8');
/* eslint-enable no-sync */

const renderTemplateOnly = (path, url, linkcss, callback) => {
  match({ routes: routes, location: url }, (err, redirect, props) => { 
    const page = template
      .replace('<!-- CONTENT -->', '')
      .replace('"-- STORES --"', '{}')
      .replace('<!-- STYLESHEET -->', (linkcss) ?
        '<link rel="stylesheet" type="text/css" href="/main.css">' :
        ''
      );
    callback(null, page);
  });
}

const renderUniversal = (path, url, linkcss, callback) => {
  const store = require('./store')();
  const state = store.getState();

  match({ routes: routes, location: url }, (err, redirect, props) => { 
    const rendered = renderToString(
      <Provider store={ store }>
        <RouterContext {...props} />
      </Provider>
    );

    const page = template
      .replace('<!-- CONTENT -->', rendered)
      .replace('"-- STORES --"', JSON.stringify(state))
      .replace('<!-- STYLESHEET -->', (linkcss) ? 
        '<link rel="stylesheet" type="text/css" href="/main.css">' :
        ''
      );

    callback(null, page);
  });
}

module.exports = { renderUniversal, renderTemplateOnly };

